import TextFunc from './inputs/day6';
import sleep from '../../functions';
import '../../App.css'
import React, { useEffect, useState, useRef } from 'react'

function hasRepeats (str) {
    return /(.).*\1/.test(str);
}

function wait(i) {
    return new Promise( resolve => {
        setTimeout(() => {resolve('')}, i)
    })
}

function Day62022Solutions() {
    
    var lenght = 0, runCheck = 'midS6Running'
    var n = 0, preDet = 4, str = ''
    var len = str.length
    var subStr = [str.slice(0, 0), str.slice(0, preDet), str.slice(preDet, -1)]
    var count = useRef(subStr[1])
    var subStrC = useRef(subStr)
    var lengthC = useRef(0)
    var runCheckC = useRef(runCheck)
    useEffect(() => {
        async function test() {
            for(var i = 0; i<len; i++) {
                subStrC.current = [str.slice(0, i), str.slice(i, i+preDet), str.slice(i+preDet, -1)]
                count.current = subStrC.current[1]
                var checklist = [];
                for(var k = 0; k<preDet; k++) {
                    checklist.push(str[i+k])
                }
                var repeats = hasRepeats(count.current)
                console.log(repeats)
                console.log(count.current)
                if (!hasRepeats(count.current)) {
                    lengthC.current = i+preDet
                    runCheckC.current = 'midS6Done'
                    console.log(lenght)
                    return subStrC.current
                }
                await wait(1000)
            }
        }
        subStr = test()
    }, [])
    var retStr = <p>{subStrC.current[0]}<span className={runCheck}>{count.current}</span>{subStrC.current[2]}</p>

    var retDiv = (
        <div>
            {/* <TextFunc /> */}
            {retStr}
            <div></div>
            <p>The amount of chars before start-of-packet marker: {lengthC.current}</p>
            <p>{str.length}</p>
        </div>
    )
    return retDiv

}

export default Day62022Solutions;